package sec06;

public class SelfProject240325n002 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 100;
		System.out.println(x + "부터" + 200);
		System.out.println(x + 100 + "year");
		System.out.println("200"+"100"+"어이쿠!");
		
		int i1 = 0x11, i2 = 5;
		final int ONE = 1;
		char c1 ='a';
		float f1 = 1.5f;
		double d1 = 2.8;
		boolean b1 = true;
		
		System.out.printf("i1 = %d\n",i1);
		System.out.printf("i1/2 = %d\n",8);
		System.out.println("c1 + ONE = " + c1 + ONE);
		System.out.printf("(c1 + ONE) = %c\n",c1 + ONE);
		System.out.printf("i2 + f1 = %f\n",i2 + f1);
		System.out.printf("f1 + d1 = %.1f\n",f1 + d1);
		System.out.printf("(int)(f1 + d1) = %d\n",(int)f1 + (int)d1);
		System.out.println("b1 = " + b1);
	}

}
